update.foreach:
update opm_mdt_acctdet_trd

	check_flag='N',
	check_memo =#{item.checkMemo,jdbcType=VARCHAR}

WHERE trd_no = #{item.trdNo,jdbcType=VARCHAR} and seq_id = #{item.seqId,jdbcType=VARCHAR}

mapper.update:
		  UPDATE opm_mdt_acctdet_trd t
                     SET t.check_flag = 'N',
                         t.check_memo = #{checkMemo,jdbcType=VARCHAR}
                   WHERE trd_no = #{trdNo,jdbcType=VARCHAR}
                     AND t.check_flag = 'Y'

MERGE INTO opm_mdt_acctdet_trd t1 USING (
SELECT t.trd_no, t.line_num, (SELECT nvl(MAX(amt), 0) as v_amt FROM opm_mdt_acctdet_inf t WHERE seq_id = (SELECT MAX(seq_id) FROM (SELECT seq_id, rownum rw FROM (SELECT seq_id FROM opm_mdt_acctdet_inf t WHERE t.act_id = #{actId,jdbcType=VARCHAR} AND t.valid_flag = 'Y' ORDER BY op_date DESC, seq_id DESC))
WHERE rw = 1)) + SUM(nvl(in_trade_amt, 0) - nvl(out_trade_amt, 0)) over(PARTITION BY trd_no ORDER BY trd_no, t.line_num rows BETWEEN unbounded preceding AND CURRENT ROW) amt_cal, ROWID id FROM opm_mdt_acctdet_trd t WHERE trd_no =#{trdNo,jdbcType=VARCHAR} GROUP BY t.trd_no, t.line_num, in_trade_amt, out_trade_amt, ROWID) t2 ON (t2.trd_no = t1.trd_no AND t2.line_num = t1.line_num AND t1.rowid = t2.id) WHEN MATCHED THEN UPDATE SET t1.amt_cal = t2.amt_cal WHERE t1.trd_no = #{trdNo,jdbcType=VARCHAR}
